import React, {useState} from 'react';
import "./ExpenseForm.css"

const ExpenseForm = () => {
    const [enteredTitle, setenteredTitle] = useState("");
    const [enteredAmouunt, setenteredAmound] = useState("");
    const [enteredDate, setenteredDate] = useState("");
    const titlechangehandler = (event) =>{
        setenteredTitle(event.target.value);
        //console.log(event.target.value);
        //console.log(enteredTitle);
    }

    return <form>
        <div className="new-expense__controls">
        <div className="new-expense__control">
            <label>Title</label>
            <input onChange={inputhandler} type="text" ></input>
        </div>

        <div className="new-expense__control">
            <label>Amount</label>
            <input onChange={inputhandler}  type="number" min="0.01" step="0.01"  ></input>
        </div>

        <div className="new-expense__control">
            <label>Date</label>
            <input onChange={inputhandler}  type="date" min="2020-01-13" max="2022-01-13" ></input>
        </div>

        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>
}
export default ExpenseForm
