import React, {useState} from 'react';
import "./ExpenseForm.css"

const ExpenseForm = () => {
    const [enteredTitle, setenteredTitle] = useState("typo");
    const [enteredAmount, setenteredAmount] = useState("");
    const [enteredDate, setenteredDate] = useState("");
    //console.log(enteredTitle);
    const titleChangeHandler = (event) =>{
        setenteredTitle(event.target.value);
        //console.log(event.target.value);
        //console.log(enteredTitle);
    }
    const AmountChangeHandler = (event) =>{
        setenteredAmount(event.target.value);
        //console.log(event.target.value);
        //console.log(enteredTitle);
    }
    const DateChangeHandler = (event) =>{
        setenteredDate(event.target.value);
        //console.log(event.target.value);
        //console.log(enteredTitle);
    }

    const submitHandler = (event) =>{
        event.preventDefault();
        const expenseData ={
            title: enteredTitle,
            amount: enteredAmount,
            data: (enteredDate)
        }
        console.log(enteredTitle);
        console.log(expenseData);
    }

    return (
    <form onSubmit={submitHandler}>
        <div className="new-expense__controls">
        <div className="new-expense__control">
            <label>Title</label>
            <input onChange={titleChangeHandler} type="text" ></input>
        </div>

        <div className="new-expense__control">
            <label>Amount</label>
            <input onChange={AmountChangeHandler} value={enteredTitle}  type="number" min="0.01" step="0.01"  ></input>
        </div>

        <div className="new-expense__control">
            <label>Date</label>
            <input onChange={DateChangeHandler} value={enteredTitle}  type="date" min="2020-01-13" max="2022-01-13" ></input>
        </div>

        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
    </form>
    )
}
export default ExpenseForm
